mapper.select:
	select
    
    from tb_order o
	where 1=1
    
 		and o.apply_id = #{order.applyId}
    
 	
 		and o.status = #{order.status}
 	
 	
 		and o.order_no = #{order.orderNo}
 	
 	
 		and str_to_date(o.apply_date,'%Y-%m-%d') <= str_to_date(#{order.applyDateBegin},'%Y-%m-%d')
 	
 	
 		and str_to_date(o.apply_date,'%Y-%m-%d') <= str_to_date(#{order.applyDateEnd},'%Y-%m-%d')
